import React, { Component } from "react";

import Toolbar from "./components/Toolbar/Toolbar";
import SideDrawer from "./components/SideDrawer/SideDrawer";
import Backdrop from "./components/Backdrop/Backdrop";
import ProjectsList from "./components/ProjectsList/ProjectsList";
import about_images from "./infos/about/images";
import Title from "./components/Title/Title";
import Carousel from "./components/Carousel/Carousel";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

class App extends Component {
  state = {
    sideDrawerOpen: false
  };

  drawerToggleClickHandler = () => {
    this.setState(prevState => {
      return { sideDrawerOpen: !prevState.sideDrawerOpen };
    });
  };

  backdropClickHandler = () => {
    this.setState({ sideDrawerOpen: false });
  };

  render() {
    let backdrop;
    if (this.state.sideDrawerOpen) {
      backdrop = <Backdrop click={this.backdropClickHandler} />;
    }
    return (
      <div style={{ height: "100%" }}>
        <Container>
          <Row>
            <Col>
              <Toolbar drawerClickHandler={this.drawerToggleClickHandler} />
            </Col>
            <Col>
              <SideDrawer show={this.state.sideDrawerOpen} />
              {backdrop}
            </Col>
          </Row>
        </Container>
        <main style={{ marginTop: "6vw" }}>
          <Row>
            <Col sm={12} lg={12}>
              <Title
                name="Ali Hedayatnia"
                university="University of Tehran"
                field="Computer Architecture"
                extera="Deep Learning and Computer Vision Researcher"
              />
            </Col>
          </Row>
          <div className="app__others">
            {/* <p>This is page content!</p>
            <p>This is page content!</p> */}
            {/* <p>{about_images}</p> */}
            <Row>
              <Col lg={{ span: 1, offset: 3 }} sm={12}>
                <Carousel images={about_images} />
              </Col>
            </Row>
            {/* <About /> */}
            {/* <ProjectsList /> */}
          </div>
        </main>
      </div>
    );
  }
}

export default App;
