import React from "react";

import "./SideDrawer.css";
import SideDrawerButton from "./SideDrawerButton";
import SideDrawerAvatar from "./SideDrawerAvatar";
import SocialNetwork from "./SideDrawerSocialNetwork";
import { faCoffee, faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { faLinkedinIn } from "@fortawesome/free-brands-svg-icons";

const sideDrawer = props => {
  let drawerClasses = "side-drawer";
  if (props.show) {
    drawerClasses = "side-drawer open";
  }
  return (
    <nav className={drawerClasses}>
      <div>
        <ul>
          <li>
            <SideDrawerAvatar
              image="./images/avatar/avatar.jpg"
              name="Ali Hedayatnia"
              university="University of Tehran"
              field="Computer Architecture"
            />
          </li>
          <li>
            <SideDrawerButton content="Timeline" icon={faCoffee} />
          </li>
          <li>
            <SideDrawerButton content="Projects" icon={faCoffee} />
          </li>
          <li>
            <SideDrawerButton content="About" icon={faCoffee} />
          </li>
        </ul>
      </div>
      {/* <div className="side-drawer__spacer" /> */}
      <div className="side-drawer__footer">
        <footer>
          <ul>
            <li>
              <SocialNetwork link="/" icon={faLinkedinIn} />
            </li>
            <li>
              <SocialNetwork link="/" icon={faLinkedinIn} />
            </li>
            <li>L</li>
          </ul>
        </footer>
      </div>
    </nav>
  );
};

export default sideDrawer;
