import React from "react";

import {
  Navbar,
  Nav,
  NavItem,
  NavDropdown,
  MenuItem,
  Image
} from "react-bootstrap";
import DrawerToggleButton from "../SideDrawer/DrawerToggleButton";

import "./Toolbar.css";

const toolbar = props => {
  return (
    <div>
      <Navbar
        collapseOnSelect
        expand="lg"
        bg="dark"
        variant="dark"
        // bsPrefix="toolbar__navbar"
      >
        <button
          aria-controls="responsive-navbar-nav"
          type="button"
          aria-label="Toggle navigation"
          onClick={props.drawerClickHandler}
          className="toolbar_toggle-button"
        >
          <span class="navbar-toggler-icon" />
        </button>

        <Navbar.Brand href="#home">
          <a href="/">
            <Image
              src={props.info.avatar}
              alt="Avatar"
              roundedCircle
              className="toolbar__avatar"
            />
          </a>
        </Navbar.Brand>
        {/* <Navbar.Toggle onClick={props.drawerClickHandler} /> */}
        <div className="toolbar__spacer" />
        <Navbar.Collapse className="toolbar__collapse" fixed>
          <Nav className="justify-content-end">
            {props.info.items.map(item => (
              <Nav.Item>
                <Nav.Link href={item.link}>{item.title}</Nav.Link>
              </Nav.Item>
            ))}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </div>
  );
};

export default toolbar;
