import React from "react";

import "./Title3.css";

const title = props => {
  return (
    <div id="carousel-area">
      <div
        id="carousel-slider"
        className="carousel slide carousel-fade"
        data-ride="carousel"
      >
        <ol className="carousel-indicators">
          <li
            data-target="#carousel-slider"
            data-slide-to="0"
            className="active"
          />
        </ol>
        <div className="carousel-inner" role="listbox">
          <div
            className="carousel-item active"
            style={{
              backgroundImage: "url('https://behesht.me/img/slider/bg-3.jpg')"
            }}
          >
            <div className="carousel-caption text-center">
              <h3
                className="wow fadeInDown animated"
                data-wow-delay="0.3s"
                style={{
                  visibility: "visible",
                  webkitAnimationDelay: "0.3s",
                  mozAnimationDelay: "0.3s",
                  animationDelay: "0.3s"
                }}
              >
                راهِ هموارِ بهشت
              </h3>
              <h2
                className="wow bounceIn animated"
                data-wow-delay="0.6s"
                style={{
                  visibility: "visible",
                  webkitAnimationDelay: "0.6s",
                  mozAnimationDelay: "0.6s",
                  animationDelay: "0.6s"
                }}
              >
                {props.name}
              </h2>
              <h4
                className="wow fadeInUp animated"
                data-wow-delay="0.9s"
                style={{
                  visibility: "visible",
                  webkitAnimationDelay: "0.9s",
                  mozAnimationDelay: "0.9s",
                  animationDelay: "0.9s"
                }}
              >
                {props.extera}
              </h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default title;
